INSERT INTO book (isbn, title, author, thumbnail, description, keywords, created_at, updated_at) VALUES ('9788954682152', '작별하지 않는다 (한강 소설ㅣ2024년 노벨문학상 수상작가 l 2023년 메디치 외국문학상 수상)', '한강', 'https://shopping-phinf.pstatic.net/main_3243636/32436366634.20231124160335.jpg', '무엇을 생각하면 견딜 수 있나.
