INSERT INTO book_ai_sample (book_isbn, style_prompt, content, created_at, updated_at) VALUES ('9791193138861', 'Logical', '책 ''고래''는 무엇이든 만들어 내는 거미 씨와 무엇이든 품어 주고픈 숲속 친구들의 이야기를 담고 있습니다.', NOW(), NOW());
