INSERT INTO book (isbn, title, author, thumbnail, description, keywords, created_at, updated_at) VALUES ('9788954699914', '하얼빈 (김훈 장편소설)', '김훈', 'https://shopping-phinf.pstatic.net/main_3368498/33684983664.20241213070908.jpg', '‘우리 시대 최고의 문장가’ ‘작가들의 작가’로 일컬어지는 소설가 김훈의 신작 장편소설 『하얼빈』이 출간되었다. 『하얼빈』은 김훈이 작가로 활동하는 내내 인생 과업으로 삼아왔던 특별한 작품이다. 작가는 청년 시절부터 안중근의 짧고 강렬했던 생애를 소설로 쓰려는 구상을 품고 있었고, 안중근의 움직임이 뿜어내는 에너지를 글로 감당하기 위해 오랜 시간을 들여 ‘인간 안중근’을 깊이 이해해나갔다. 그리고 2022년 여름, 치열하고 절박한 집필 끝에 드디어 그 결과물을 세상에 내놓게 되었다.', '김훈, 하얼빈, 안중근, 작가, 신작, 장편소설', NOW(), NOW());
INSERT INTO book_ai_sample (book_isbn, style_prompt, content, created_at, updated_at) VALUES ('9788954699914', 'Legal', '『하얼빈』은 김훈이 안중근의 생애를 소설로 쓰려는 작품이다.', NOW(), NOW());
INSERT INTO book_ai_sample (book_isbn, style_prompt, content, created_at, updated_at) VALUES ('9788954699914', 'Emotional', '『하얼빈』은 작가의 치열하고 절박한 집필 노력이 느껴진다.', NOW(), NOW());
