INSERT INTO book_ai_sample (book_isbn, style_prompt, content, created_at, updated_at) VALUES ('9791130324418', 'Logical', '이 책은 노동법의 원리·제도·판례를 체계적으로 정리하여 개별적·집단적·협력적 노사관계를 종합적으로 이해하도록 돕는다. 현행 법제와 최근 개정 동향까지 반영해 노동현실 변화 속에서 노동법이 수행하는 기능과 쟁점을 균형 있게 설명한다.', NOW(), NOW());
