INSERT INTO book (isbn, title, author, thumbnail, description, keywords, created_at, updated_at) VALUES ('9781967424054', '64+ (Your Ultimate Guide to Medicare & Retirement Planning)', '', 'https://shopping-phinf.pstatic.net/main_5697657/56976576153.20250928201455.jpg', 'Medicare shouldn''t be complicated. Yet, too many people feel overwhelmed by confusing options, unexpected costs, and last-minute decisions. 64+ is your clear, step-by-step guide to navigating Medicare and retirement planning with confidence. Written by industry expert Chase Gruening, this book simplifies enrollment, breaks down coverage costly mistakes.', 'Medicare, retirement planning, enrollment, coverage, mistakes, confidence', NOW(), NOW());
