INSERT INTO book_ai_sample (book_isbn, style_prompt, content, created_at, updated_at) VALUES ('9788954442688', 'Action', '『삼체』를 읽으면서 우주의 미지를 탐험하고 인류의 운명에 대한 상상력을 키워가며, SF 작품 속에서의 생존전쟁을 경험할 수 있다.', NOW(), NOW());
INSERT INTO book (isbn, title, author, thumbnail, description, keywords, created_at, updated_at) VALUES ('9788935704774', 'ET 컨택트 (변화를 앞둔 인류에게 전하는 외계인 메시지)', '지나 레이크', 'https://shopping-phinf.pstatic.net/main_5502077/55020779257.20250531092057.jpg', '외계인들은 인류가 존재하기 전부터
