INSERT INTO book (isbn, title, author, thumbnail, description, logic_score, emotion_score, action_score, created_at,
                  updated_at)
VALUES ('9791198896162', '투자디톡스 + 필사노트 세트 (본성에 휘둘리지 않고 불안에서 벗어나기)', '문홍철',
        'https://shopping-phinf.pstatic.net/main_5667055/56670558530.20250911093557.jpg', '타인과 자신의 감정 소용돌이에 몸과 마음을 뺏겨 표류하지 말자!
〈투자디톡스〉는 경제를 다루는 다른 책들과는 달리 인간 우선의 관점을 설명하며, 과거 경제 역사, 인간의 본성을 다루는 내용을 중심으로 구성되어 있다. 인간에 대해 이해하려고 노력하는 사람은 필연적으로 자기 자신을 마주할 수밖에 없을 것이다. 스스로에 대한 메타적 인식은 특히 투자에 있어서 실수를 방지하는 데 최고의 도구가 된다.

투자에 정답은 없지만 오답은 있다!
시대의 유행으로부터 벗어나 기본으로 돌아가는 단순하지만 장기적이고 정답 맞히기가 아닌 오답을 제거하는 투자법을 안내해 줄 것이다.', 75, 60, 70, NOW(), NOW());
INSERT INTO book_ai_sample (book_isbn, style_prompt, content, created_at, updated_at)
