INSERT INTO book_ai_sample (book_isbn, style_prompt, content, created_at, updated_at)
VALUES ('9791160263404', 'Summary',
        '에이해브 선장은 모비 딕에게 다리를 잃고 복수를 결심한다. 피쿼드 호의 선원들과 함께 고래를 쫓아 바다를 누비며, 복수의 순간이 파멸로 이어진다. 인간의 본성, 선과 악의 갈등을 탐구하는 이 작품은 문학적 가치가 크다.',
        NOW(), NOW());
INSERT INTO book (isbn, title, author, thumbnail, description, logic_score, emotion_score, action_score, created_at,
                  updated_at)
VALUES ('9791194928249', '초판본 빨강 머리 앤 영문판 세트', '루시 모드 몽고메리',
        'https://shopping-phinf.pstatic.net/main_5664739/56647398416.20250917071510.jpg', '국내 유일 빨강 머리 앤 영문판 세트
“100년 전, 우리가 사랑한 앤의 진짜 모습을 다시 만나다”

‘빨간 머리 앤’이라 하면 초록 지붕 집에 사는 천진난만한 소녀가 우리의 마음을 밝히며 환하게 떠오르는 모습이 떠오를 것이다. 그렇다면 《레드먼드의 앤》이나 《잉글사이드의 앤》은 어떨까. 어딘가 낯설게 느껴지지는 않는지? 우리가 알고 있었던 빨간 머리 앤은 ‘빨간 머리 앤’ 시리즈의 1권인 《초록지붕집의 앤》의 내용만을 담은 것이기 때문이다.
햇빛을 받아 눈부시게 빛나는 사파이어 같은 바다, 캐나다 세인트 로렌스 만에 반달형으로 뜬 프린스 에드워드 섬, 싱그러운 초록빛 나무들, 잘 익은 과일처럼 붉고 꼬불꼬불한 마을 오솔길, 탐스러운 열매가 주렁주렁 매달린 과수원, 사과꽃 흐드러지게 핀 환희의 길, 이 세상 모든 여자들이 꿈꾸는 행복한 집 잉글사이드…
앤의 낭만적 인생 이야기들은 이 아름다운 섬을 무대로 소소하게 펼쳐진다. 소녀 시절부터 할머니에 이르는 앤 셜리의 소박한 삶은 한 여성의 아름다운 이야기이다. 꿈과 사랑 속에서 자식을 키우는 앤의 일생은 평범하면서도 진솔하고, 삶의 따뜻한 의미가 우물처럼 깊게 담겨 있다. 여자로서 살아가며 겪게 되는 사랑의 갈등, 삶의 고뇌, 행복이 떠나간 자리에 남은 아픔과 고독이 섬세한 필치로 전8권에 걸쳐 그려져 있다.',
        20, 80, 30, NOW(), NOW());
INSERT INTO book_ai_sample (book_isbn, style_prompt, content, created_at, updated_at)
