INSERT INTO book (isbn, title, author, thumbnail, description, logic_score, emotion_score, action_score, created_at,
                  updated_at)
VALUES ('9791194591801', 'Anne of Ingleside: 잉글사이드의 앤 (1939년 오리지널 초판본 표지 디자인) (1939년 오리지널 초판본 표지 디자인)', '루시 모드 몽고메리',
