INSERT INTO book_ai_sample (book_isbn, style_prompt, content, created_at, updated_at)
VALUES ('9791194591801', 'Logical',
        '《잉글사이드의 앤》은 앤 셜리 시리즈의 10번째 책으로, 시리즈의 시퀀스를 볼 때 흥미로운 위치를 차지하고 있습니다. 이 책은 앤의 삶의 후반부를 다루며, 캐릭터의 발전을 논리적으로 분석할 수 있는 기회를 제공합니다.',
