INSERT INTO book (isbn, title, author, thumbnail, description, logic_score, emotion_score, action_score, created_at,
                  updated_at)
VALUES ('9788959728800', '리더십 (글로벌 시대의 리더가 갖추어야 할 역량)', '변상우',
        'https://shopping-phinf.pstatic.net/main_3286445/32864453640.20230815080411.jpg', '글로벌 시대에 요구하는 존경받는 리더가 되기 위해서는 리더십의 스킬과 행동보다는 리더의 자질과 기업가정신, 철학 등에 대한 이해와 이를 통해 자신이 그들에 비해 부족한 부분을 보충하고 개발하는 데 더 많은 노력을 해야 한다. 단순히 리더십 이론과 바람직한 리더의 행동을 따라한다고 다 훌륭한 리더가 되고 높은 수준의 성과를 달성하는 것이 아니라는 점을 깨달아야 한다.
본서는 리더가 되기를 희망하는 모든 사람들과 현재 리더의 자리에서 조직을 올바른 방향으로 이끌어가고자 하는 사람들에게 가장 쉽고, 가장 명료하게, 가장 핵심이 되는 내용으로 구성되어 있다.', 85, 30, 60,
        NOW(), NOW());
INSERT INTO book_ai_sample (book_isbn, style_prompt, content, created_at, updated_at)
