INSERT INTO book (isbn, title, author, thumbnail, description, logic_score, emotion_score, action_score, created_at,
                  updated_at)
VALUES ('9791157064816', 'F1 리더십 (속도를 사유하라, 가속과 멈춤의 비즈니스 전략)', '변동식',
        'https://shopping-phinf.pstatic.net/main_5725297/57252971130.20251017092840.jpg', '“제어할 수 없는 것은 속도가 아니다.”

속도를 늦추고 생각을 가속하라
F1에서 배우는 가속과 멈춤의 비즈니스 전략

“제어할 수 없는 것은 속도가 아니다.”
“우승하려면 먼저 완주해야 한다.”
“레이스의 진짜 주인공은 ‘변수’다.”
“실패는 성장의 종말이 아닌 성장의 본질이다.”

F1의 이 단순한 진리는 오늘날 불확실성의 시대를 살아가는 리더들에게 가장 현실적인 경고이자 조언이다. 《F1 리더십》은 모터스포츠의 극한 현장에서 길어 올린 통찰을 경영과 조직의 언어로 옮긴 책이다. 저자는 대기업 CEO이자 대한자동차경주협회장으로 활동하며 속도와 데이터, 리스크와 협업, 통제와 리더십의 균형을 몸으로 배웠다. 그가 말하는 리더십은 더 빠르게 가는 기술이 아니라, 위기에서도 흔들리지 않고 완주하는 제어의 기술이며, 속도를 지탱하는 지혜다. 《F1 리더십》은 오늘의 경영자들에게 “가속보다 통제, 속도보다 균형”이라는 새로운 리더십의 기준을 제시한다.
책 곳곳에는 생생한 F1 현장 사례와 함께 국내외 기업들의 구체적인 경영 사례가 함께 펼쳐진다. 브레이크 과열로 기권한 알론소, 위험을 통제해 완주한 맥라렌 팀. 브레이크 없는 확장으로 무너진 위워크와 데이터를 통해 위기를 기회로 바꾼 쿠팡과 지멘스. 저자는 “속도를 내는 일보다, 그 속도를 안전하게 이어가는 기술이 리더십의 본질”임을 역설한다.',
        75, 30, 60, NOW(), NOW());
INSERT INTO book_ai_sample (book_isbn, style_prompt, content, created_at, updated_at)
VALUES ('9791157064816', 'Logical',
        'F1 리더십은 경영 전략을 F1 레이스의 가속과 통제에서 배우는 독특한 접근을 제시합니다. 속도는 단순한 목표가 아니라, 통제와 균형을 통해 지속 가능하게 유지해야 한다는 점이 인상적입니다. 다양한 사례를 통해 실천적 교훈을 제공하는 점이 논리적 사고를 자극합니다.',
        NOW(), NOW());
INSERT INTO book_ai_sample (book_isbn, style_prompt, content, created_at, updated_at)
